function CountdownTimer(e,t,n){this.initialize.apply(this,arguments)}function CDT(){var e=new Date("2020/01/01 00:00:00"),t=new CountdownTimer("CDT",e,'<span class="number-wrapper"><div class="line"></div><span class="number end">Time is up!</span></span>');t.countDown()}CountdownTimer.prototype={initialize:function(e,t,n){this.elem=document.getElementById(e);this.tl=t;this.mes=n},countDown:function(){var e="",t=new Date,n=Math.floor((this.tl-t)/864e5),r=Math.floor((this.tl-t)%864e5/36e5),i=Math.floor((this.tl-t)%864e5/6e4)%60,s=Math.floor((this.tl-t)%864e5/1e3)%60%60,o=this;if(!(this.tl-t>0)){this.elem.innerHTML=this.mes;return}e+='<span class="number-wrapper"><div class="line"></div><div class="caption">DAYS</div><span class="number day">'+n+"</span></span>";e+='<span class="number-wrapper"><div class="line"></div><div class="caption">HOURS</div><span class="number hour">'+r+"</span></span>";e+='<span class="number-wrapper"><div class="line"></div><div class="caption">MINS</div><span class="number min">'+this.addZero(i)+'</span></span><span class="number-wrapper"><div class="line"></div><div class="caption">SECS</div><span class="number sec">'+this.addZero(s)+"</span></span>";this.elem.innerHTML=e;tid=setTimeout(function(){o.countDown()},10)},addZero:function(e){return("0"+e).slice(-2)}};window.onload=function(){CDT()};